import telebot
from telebot import types
bot = telebot.TeleBot('')

@bot.message_handler(commands=['start'])
def start(message):
    file = open('grammatick_servis.jpg', 'rb')
    mess = "<b>–ü—Ä–∏–≤–µ—Ç!</b> –†–∞–¥—ã –ø—Ä–∏–≤–µ—Ç—Å—Ç–≤–æ–≤–∞—Ç—å —Ç–µ–±—è –≤ –Ω–∞—à–µ–º –±–æ—Ç–µ –¥–ª—è –ø–æ–º–æ—â–∏ –≤ –∏–∑—É—á–µ–Ω–∏–∏ —Ä—É—Å—Å–∫–æ–≥–æ —è–∑—ã–∫–∞ –∏ –ø–æ–¥–≥–æ—Ç–æ–≤–∫–µ –∫ —ç–∫–∑–∞–º–µ–Ω–∞–º –ø–æ —ç—Ç–æ–º—É –ø—Ä–µ–¥–º–µ—Ç—É. –ß—Ç–æ —Ç–µ–±—è –∏–Ω—Ç–µ—Ä–µ—Å—É–µ—Ç?"
    markup = types.InlineKeyboardMarkup()
    button1 = types.InlineKeyboardButton("–û–ì–≠", callback_data="OGE")
    button2 = types.InlineKeyboardButton("–ï–ì–≠", callback_data="EGE")
    button3 = types.InlineKeyboardButton("–ò—Ç–æ–≥–æ–≤–æ–µ —Å–æ–±–µ—Å–µ–¥–æ–≤–∞–Ω–∏–µ", callback_data="it_sob")
    button4 = types.InlineKeyboardButton("–ò—Ç–æ–≥–æ–≤–æ–µ —Å–æ—á–∏–Ω–µ–Ω–∏–µ", callback_data="it_soch")
    markup.add(button1)
    markup.add(button2)
    markup.add(button3)
    markup.add(button4)
    bot.send_photo(message.chat.id, file, mess, reply_markup=markup, parse_mode='HTML')


@bot.message_handler(commands=['help'])
def help(message):
    mess = "–ü–æ —Å—Å—ã–ª–∫–µ –Ω–∏–∂–µ —Ç—ã –ø–æ–ª—É—á–∏—à—å –¥–æ—Å—Ç—É–ø –∫ —Å–ª–æ–≤–∞—Ä—è–º –∏ —Ñ–æ—Ä—É–º–∞–º, –Ω–∞ –∫–æ—Ç–æ—Ä—ã—Ö –æ–±—Å—É–∂–¥–∞—é—Ç—Å—è —Ç—Ä—É–¥–Ω—ã–µ –≤–æ–ø—Ä–æ—Å—ã —è–∑—ã–∫–æ–∑–Ω–∞–Ω–∏—è: https://clck.ru/33o2aH"
