@bot.message_handler(commands=['question'])
def question(message):
    mess = "–ú—ã —Å —Ä–∞–¥–æ—Å—Ç—å—é –ø–æ–º–æ–∂–µ–º —Ç–µ–±–µ –≤ –∫—Ä–∞—Ç—á–∞–π—à–∏–µ —Å—Ä–æ–∫–∏! –ù–∞–ø–∏—à–∏ –Ω–∞–º –≤ Telegram: @ts_gramotnyeservisy_bot."
    bot.send_message(message.chat.id, mess, parse_mode='html')

@bot.message_handler(content_types=["text"])
def message_user(message):
    if message != "/start" and message != "/help" and message != "/question":
        bot.send_message(message.chat.id, "–Ø –µ—â—ë –Ω–µ –∑–Ω–∞—é –∫–∞–∫ –æ—Ç–≤–µ—Ç–∏—Ç—å –Ω–∞ —ç—Ç—É –∫–æ–º–∞–Ω–¥—É üëæ")

